import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.min.js"
import "./CSS/Index.css";
import Header from "./Components/Header";
import Navbar from "./Components/Navbar";
import { Route, Routes, useLocation } from "react-router-dom";
import Home from "./Components/Home";
import Footer from "./Components/Footer";
import Products from "./Components/Products";
import Crops from "./Components/Crops";
import Career from "./Components/Career";
import Certificate from "./Components/Certificate";
import About from "./Components/About";
import Contact from "./Components/Contact.jsx";
import Topbutton from "./Components/Topbutton.jsx";
import Management from "./Components/Management.jsx";


const App = () =>{
    const {pathname} = useLocation();
    
    const scrollToTop = () => {
        window.scrollTo(0, 0); // Scroll to the top of the page
      };
    
    return(
        <>
           <Header />
           <Navbar />
           <Routes scrollRestoration="auto">
                  <Route exact path="/" element={<Home />}  onClick={scrollToTop}/>
                  <Route path="/product" element={<Products />} onClick={scrollToTop}/>
                  <Route path="/crop" element={<Crops />} onClick={scrollToTop}/>
                  <Route path="/career" element={<Career />} onClick={scrollToTop}/>
                  <Route path="/certificate" element={<Certificate />} onClick={scrollToTop}/>
                  <Route path="/about" element={<About />} onClick={scrollToTop}/>
                  <Route path="/contact" element={<Contact />} onClick={scrollToTop}/>
                  <Route path="/management" element={<Management />} onClick={scrollToTop}/>
                  
     
           </Routes>
           <Footer />
           <Topbutton />
        
        

        </>
    )
}

export default App;
