import React, { useEffect } from "react";
import { NavLink, useLocation } from "react-router-dom";


const SubTextBox = ({subheading, heading, para, imgsrc, bg, tOrder, pOrder, btn, btnlink}) =>{
    const location = useLocation();

  useEffect(() => {
    window.scrollTo(0, 0); 
  }, [location.pathname])

    return(
        <>
            <div className="TextBox" style={{backgroundColor: bg}}>
            <div className="container">
                <div className="row">
                <div className={`col-lg-6 col-md-12 order-lg-${tOrder} order-md-1  SubTextBox-1`} >
            <h4>{subheading}</h4>
            <h3>{heading}</h3>
            <p>{para}</p>
            {btn && (<NavLink to={`/${btnlink}`} ><button className="btn">{btn}</button></NavLink>)}
            
            </div>
            <div className={`col-lg-6 col-md-12 order-lg-${pOrder} order-md-2 SubTextBox-2 `}>
            
                <img src={imgsrc} alt="tractor" className="textboxImg-1" />
            
            
            </div>
                
                    

                </div>
            </div>
        </div>
        </>

    )
}

export default SubTextBox;
